`,(0,r.jsxDEV)(n.pre,{children:(0,r.jsxDEV)(n.code,{className:"language-css",children:`.element {
  height: 150px;
  width: 150px;
  margin: 0 auto;
  background: red;
  animation: rotateElement 2.5s ease-in-out infinite;
}

.animation-container {
  display: flex;
  align-items: center;
  min-height: 50vh;
}

@keyframes rotateElement {
  0% {
    transform: scale(1);
    border-radius: 20%;
  }
  15% {
    transform: scale(1) rotate(0deg);
    border-radius: 20%;
  }
  30% {
    transform: scale(2) rotate(0deg);
    border-radius: 20%;
  }
  45% {
    transform: scale(2) rotate(0deg);
    border-radius: 20%;
  }
  60% {
    transform: scale(2) rotate(270deg);
    border-radius: 50%;
  }
  75% {
    transform: scale(1) rotate(270deg);
    border-radius: 50%;
  }
  100% {
    transform: scale(1) rotate(0deg);
    border-radius: 20%;
  }
}
